import React, { Component } from 'react';
import styled from '@emotion/styled';
import Link from 'next/link';
// import { Global, css } from '@emotion/core'
import "../style.css"

const NavbarWrapper = styled.div`
position: fixed;
z-index: 9;
left: 0;
top: 0;
background-color: #2d2d2d;
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;
width: 100%;
box-sizing: border-box;
height: 100px;
transition: all 0.5s ease-in;
padding: 0rem .8rem 0rem .8rem;
color: white;   

.logo img{
    width: ${this.state.logoWidth + "px"};
    transition: all 1s ease-in;

}
`

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            navbarHeight: 60,
            logoWidth: 60
        };
    }

    handleScroll = () => {
        if (pageYOffset > 100){
            this.setState({navbarHeight: 60, logoWidth: 60})
        } else if (pageYOffset < 300) {
            this.setState({navbarHeight: 45, logoWidth: 60})
        }
    }

    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    }

    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
    }

    render() {

        return(

            <NavbarWrapper onScroll={this.handleScroll}>
                <Link href="/">
                    <a>
                    <div className="logo">
                        <img src="../static/img/logo_white.svg" alt="elbilvelger" width="140" />
                    </div>
                    </a>
                </Link>

                <ul>
                    <li>Hei</li>
                    <li>Hei</li>
                    <li>Hei</li>
                </ul>

            </NavbarWrapper>

        )
    }
}


export default Navbar;
