import { calculateEventTime } from '../utils/event-time';

export default function getActivity(store: any, id: string, now: Date) {
  if (!store.activeTime[id]) {
    return { activeTime: 0, isActive: false };
  }
  const { activity } = store.activeTime[id];
  return {
    activeTime: calculateEventTime({
      times: activity,
      screenEvents: store.screen.events,
      now,
    }),
    isActive: activity[activity.length - 1].type === 'start',
  };
}
