import { VideoTimeEvent } from './video-event-time';

export function getUniqueVideoIdsForPlayer(
  events: VideoTimeEvent[],
  playerId: string,
): string[] {
  const filteredEvents = events.filter(ev => ev.playerId === playerId);
  const uniqueIds = Object.keys(
    filteredEvents.reduce((acc: { [key: string]: boolean }, curr) => {
      acc[curr.videoId] = true;
      return acc;
    }, {}),
  );
  return uniqueIds;
}
